import './JobCard.css'
import React, {useState} from 'react'
function JobCard({job}){
  const [showFullDetails, setShowFullDetails] = useState(false);

  // Function to truncate description to the first 30 words
  const truncatedDetails = job.jobDetailsFromCompany.split(' ').slice(0, 30).join(' ');
  const fullDetails = job.jobDetailsFromCompany;

  const toggleDetails = () => {
    setShowFullDetails(!showFullDetails);
  };
  return (
    <div className="group-parent">
      <div className="group-container">
        <div className="rectangle-wrapper">
          <div className="group-child" />
        </div>
        <div className="group-item" />
        <div className="group-inner" />
        <div className="posted-16-days">Posted 16 days ago</div>
        <div className="applicant">1 applicant</div>
        <b className="description">Description:</b>
        <b className="location">Location:</b>
        <b className="experience">Experience:</b>
        <b className="pinch">Pinch</b>
        <img className="ellipse-icon" alt="" src="/ellipse-1.svg" />
        <div className="founding-frontend-engineer">
          {job.jobRole}
        </div>
        <div className="india">India</div>
        <div className="rectangle-div" />
        <div className="easy-apply">Easy Apply</div>
      </div>
      <div className="estimated-salary-40-">
        Estimated Salary: {job.maxExp} Lacs
      </div>
      <b className="at-pinch-our-container">
      <b className="at-pinch-our">
          {showFullDetails ? fullDetails : truncatedDetails}
        </b>
        {truncatedDetails.length < fullDetails.length && (
          <button className='show-more' onClick={toggleDetails}>
            {showFullDetails ? 'Show Less' : 'Show More'}
          </button>
        )}
        {/* <p className="at-pinch-our">{`Our aim is to bring comfort & convenience to families in their day-to-day household chores and then take them on a journey where they find pleasure in exploring the finer aspects of`}</p> */}
      </b>
      <b className="mumbai">{job.location}</b>
      <b className="fresher">Fresher</b>
    </div>
  );
};

export default JobCard;
