import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import "./JobCard.css";

function JobCard({ job }) {
  const [showFullDetails, setShowFullDetails] = useState(false);

  const toggleFullDetails = () => {
    setShowFullDetails(!showFullDetails);
  };

  const truncatedDetails = job.jobDetailsFromCompany.split(' ').slice(0, 20).join(' ');
  const fullDetails = job.jobDetailsFromCompany;

  return (
    <div className="main">
      <div className="main-child" />
      <section className="frame-parent">
        <div className="frame-group">
          <div className="frame-container">
            <div className="rectangle-parent">
              <div className="frame-child" />
              <div className="posted-16-days">Posted 16 days ago</div>
            </div>
            <div className="rectangle-group">
              <div className="frame-item" />
              <div className="applicant">1 applicant</div>
            </div>
          </div>
          <div className="ellipse-parent">
            <div className="frame-inner" />
            <div className="pinch-parent">
              <b className="pinch">Pinch</b>
              <b className="role-frontend-engineer"  style={{textTransform: 'capitalize'}}>Role: {job.jobRole}</b>
              <b className="india">India</b>
              <div className="link" >{job.jdLink}</div>
            </div>
          </div>
          <div className="frame-wrapper">
            <div className="estimated-salary-40-50-lakh-parent">
              <div className="estimated-salary-40">
                Estimated salary: {job.maxJdSalary} - {job.minJdSalary} {job.salaryCurrencyCode}
              </div>
              <b className="description">Description:</b>
              <b className="at-pinch-our-container">
                <p className="at-pinch-our">
                  {showFullDetails ? fullDetails : truncatedDetails}
                </p>
              </b>
            </div>
          </div>
        </div>
        <div className="frame-div">
          <Popup trigger={<button className='show-more'>Show more</button>} modal nested>
          {close => (
      <div className="modal">
        <button className="close" onClick={close}>
          &times;
        </button>
        <div className="header"> Description </div>
        <div className="content">
          {' '}
          {job.jobDetailsFromCompany}
        </div>
        <div className="actions">
          <button
            className="button"
            onClick={() => {
              close();
            }}
          >
            Close
          </button>
        </div>
      </div>
    )}

          </Popup>
        </div>
      </section>
      <div className="main-inner">
        <div className="location-mumbai-parent">
          <div className="location-mumbai">
            <b>{`Location: `}</b>
            <span style={{textTransform: 'capitalize'}}>{job.location}</span>
          </div>
          <div className="experience-4-years-container">
            <b>{`Experience: `}</b>
            {job.minExp !== null && job.maxExp !== null ? (
              <span>{job.minExp} - {job.maxExp} years</span>
            ) : job.minExp !== null ? (
              <span>{job.minExp}+ years</span>
            ) : job.maxExp !== null ? (
              <span>Up to {job.maxExp} years</span>
            ) : (
              <span>Not specified</span>
            )}
          </div>
        </div>
      </div>
      <div className="main-inner1">
        <div className="vector-group">
          {/* <img
            className="frame-child1"
            loading="lazy"
            alt=""
            src="/rectangle-4.svg"
          /> */}
          <button className="easy-apply" >Easy Apply</button>
        </div>
      </div>
    </div>
  );
}

export default JobCard;
