import React from 'react';
import './JobCard.css'
const JobCard = ({ job }) => {
  return (
    <div className='job-card'>
        <div className="job-details">
        <h2>{job.jobRole}</h2>
            <div className="company-info">
                <p className="company-name">{job.companyName}</p>
                <p>{job.location}</p>
            </div>
            <p>Description: {job.jobDetailsFromCompany}</p>
      {/* <h2>Job Role: {job.jobRole}</h2>
      <p>Description: {job.jobDetailsFromCompany}</p>
      <p>Location: {job.location}</p>
      {job.minJdSalary !== null && (
        <p>Min Salary: {job.minJdSalary} {job.salaryCurrencyCode}</p>
      )}
      {job.maxJdSalary !== null && (
        <p>Max Salary: {job.maxJdSalary} {job.salaryCurrencyCode}</p>
      )}
      {job.minExp !== null && job.maxExp !== null ? (
        <p>Experience: {job.minExp} - {job.maxExp} years</p>
      ) : job.minExp !== null ? (
        <p>Experience: {job.minExp}+ years</p>
      ) : job.maxExp !== null ? (
        <p>Experience: Up to {job.maxExp} years</p>
      ) : (
        <p>Experience: Not specified</p>
      )} */}
    </div>
    <button className="easy-apply">Easy Apply</button>
    </div>
  );
}

export default JobCard;
